import productModel from "../models/product.model.js";
import multer from "multer";
import fs from "fs";
import path from "path";

const storage = multer.diskStorage({
  destination: function (req, file, cb) {
    const uploadPath = "./uploads";
    const subfolder = "products";

    // Create "uploads" folder if it doesn't exist
    if (!fs.existsSync(uploadPath)) {
      fs.mkdirSync(uploadPath);
    }

    // Create subfolder inside "uploads"
    const subfolderPath = path.join(uploadPath, subfolder);
    if (!fs.existsSync(subfolderPath)) {
      fs.mkdirSync(subfolderPath);
    }

    cb(null, subfolderPath);
  },
  filename: function (req, file, cb) {
    const name = file.originalname; // abc.png
    const ext = path.extname(name); // .png
    const nameArr = name.split("."); // [abc,png]
    nameArr.pop();
    const fname = nameArr.join("."); //abc
    const fullname = fname + "-" + Date.now() + ext; // abc-12345.png
    cb(null, fullname);
  },
});

const upload = multer({ storage: storage });

export const getProducts = async (req, res) => {
  try {
    const products = await productModel.aggregate([
      {
        $lookup: {
          from: "categories",
          localField: "category",
          foreignField: "_id",
          as: "categories",
        },
      },
      { $unwind: "$categories" },
      {
        $lookup: {
          from: "sub-categories",
          localField: "subcategory",
          foreignField: "_id",
          as: "sub-categories",
        },
      },
      { $unwind: "$sub-categories" },
    ]);
    if (products) {
      return res.status(200).json({
        data: products,
        message: "Success",
        path: process.env.PRODUCTS_PATH,
      });
    }
  } catch (error) {
    return res.status(500).json({
      message: error.message,
    });
  }
};

export const addProduct = (req, res) => {
  try {
    const uploadData = upload.fields([
      { name: "thumbnail", maxCount: 1 },
      { name: "images", maxCount: 4 },
    ]);

    uploadData(req, res, function (err) {
      if (err) return res.status(400).json({ message: err.message });

      const {
        name,
        category,
        subcategory,
        quantity,
        price,
        shortdescription,
        description,
      } = req.body;

      let thumbnail = null;
      if (req.files && req.files["thumbnail"]) {
        thumbnail = req.files["thumbnail"][0].filename;
      }

      let images = [];
      if (req.files && req.files["images"]) {
        req.files["images"].forEach((file) => {
          images.push(file.filename);
        });
      }

      // console.log('Files:', req.files);

      const productData = new productModel({
        name: name,
        category: category,
        subcategory: subcategory,
        quantity: quantity,
        price: price,
        shortdescription: shortdescription,
        description: description,
        thumbnail: thumbnail,
        images: images.join(", "),
      });

      const validationError = productData.validateSync();
      if (validationError) {
        return res.status(400).json({ message: validationError.message });
      }

      productData.save();
      if (productData) {
        return res.status(201).json({
          data: productData,
          message: "Product Added Successfully",
        });
      }
    });
  } catch (error) {
    return res.status(500).json({
      message: error.message,
    });
  }
};

export const getSingleProduct = async (req, res) => {
  try {
    const productID = req.params.product_id;
    const productData = await productModel.findOne({ _id: productID });

    if (productData) {
      return res.status(200).json({
        data: productData,
        message: "Success",
      });
    }
  } catch (error) {
    return res.status(500).json({
      message: error.message,
    });
  }
};

export const updateProduct = async (req, res) => {
  try {
    const uploadData = upload.fields([
      { name: "thumbnail", maxCount: 1 },
      { name: "images", maxCount: 4 },
    ]);

    uploadData(req, res, async function (err) {
      if (err) return res.status(400).json({ message: err.message });

      const product_id = req.params.product_id;

      const productData = await productModel.findOne({ _id: product_id });
      const {
        name,
        category,
        subcategory,
        quantity,
        price,
        shortdescription,
        description,
      } = req.body;

      let thumbnail = productData.thumbnail;
      if (req.files && req.files["thumbnail"]) {
        thumbnail = req.files["thumbnail"][0].filename;
        if (fs.existsSync("./uploads/products/" + productData.thumbnail)) {
          fs.unlinkSync("./uploads/products/" + productData.thumbnail);
        }
      }

      let images = productData.images;
      if (req.files && req.files["images"]) {
        req.files["images"].forEach((file) => {
          images.push(file.filename);
        });
        if (fs.existsSync("./uploads/products" + productData.images)) {
          fs.unlinkSync("./uploads/products" + productData.images);
        }
      }

      // console.log('Files:', req.files);

      const updatedData = await productModel.updateOne(
        { _id: product_id },
        {
          $set: {
            name: name,
            category: category,
            subcategory: subcategory,
            quantity: quantity,
            price: price,
            shortdescription: shortdescription,
            description: description,
            thumbnail: thumbnail,
            images: images
          },
        }
      );

      const validationError = productData.validateSync();
      if (validationError) {
        return res.status(400).json({ message: validationError.message });
      }

      if (updatedData) {
        return res.status(201).json({
          data: updatedData,
          message: "Product Updated Successfully",
        });
      }
    });
  } catch (error) {
    return res.status(500).json({
      message: error.message,
    });
  }
};

export const deleteProduct = async(req,res) =>{
  try {
    const product_id = req.params.product_id;

    const productData = await productModel.findOne({_id:product_id})

    let thumbnail = productData.thumbnail;
      if (req.files && req.files["thumbnail"]) {
        thumbnail = req.files["thumbnail"][0].filename;
        if (fs.existsSync("./uploads/products/" + productData.thumbnail)) {
          fs.unlinkSync("./uploads/products/" + productData.thumbnail);
        }
      }

      let images = productData.images;
      if (req.files && req.files["images"]) {
        req.files["images"].forEach((file) => {
          images.push(file.filename);
        });
        if (fs.existsSync("./uploads/products/" + productData.images)) {
          fs.unlinkSync("./uploads/products/" + productData.images);
        }
      }

      const removeData = await productModel.deleteOne({_id:product_id})
      if(removeData.acknowledged){
        return res.status(200).json({
          data:removeData,
          message:"Product Deleted Successfully"
        })
      }
  } catch (error) {
    return res.status(500).json({
      msg: error.msg,
    });
  }
}

